import React, { Component, Fragment, useState } from "react";
import axios from "axios";
import RegisterModal from "./auth/RegisterModal";
import LoginModal from "./auth/LoginModal";
import Logout from "./auth/Logout";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { searchPost } from "../actions/postActions";
import { BrowserRouter as Router } from "react-router-dom";
import "./IndexNavbar.css";

import {
  Navbar,
  Nav,
  NavItem,
} from "react-bootstrap";
class IndexNavbar extends Component {
  constructor() {
    super();
    this.state = {
      isOpen: false,
      productsCart: [],
      prixTotal: 0,
      nombreTotal: 0,
    };
  }
  static propTypes = {
    auth: PropTypes.object.isRequired,
  };

  componentDidMount() {

  }

  operation() {
    this.setState({
      isOpen: !this.state.isOpen,
    });
  }

  render() {
    const { user, isAuthenticated, isLoading } = this.props.auth;
    let id = window.location.href.split("/");
    id = id[id.length - 1];

    const authLinks = (
      <Fragment>
        <Router>
          <NavItem>
            <Logout />
          </NavItem>
        </Router>
      </Fragment>
    );

    const guestLinks = (
      <Fragment>
        <NavItem>
          <LoginModal />
        </NavItem>
        <NavItem>
          <RegisterModal />
        </NavItem>
      </Fragment>
    );

    const productsInCart = this.state.nombreTotal;

    return (
      <div id="navbarholder">
        <Navbar color="light" light="true" expand="lg" id="navbar">
          <Navbar.Brand href="/" id="brandName">
            8-commerce
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
        </Navbar>
        <Navbar id="underline">
          <Nav>
            {!isLoading ? (isAuthenticated ? authLinks : guestLinks) : null}
          </Nav>
        </Navbar>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  post: state.post,
  auth: state.auth,
});

export default connect(mapStateToProps, { searchPost })(IndexNavbar);
